import React from "react";
// @material-ui/core components
import { makeStyles } from "@material-ui/core/styles";

// core components
import GridContainer from "components/Grid/GridContainer.js";
import GridItem from "components/Grid/GridItem.js";

import styles from "assets/jss/nextjs-material-kit/pages/landingPageSections/productStyle.js";

const useStyles = makeStyles(styles);

export default function AboutMe() {
  const classes = useStyles();
  return (
    <div className={classes.section}>
      <GridContainer justify="center">
        <GridItem xs={12} sm={12} md={8}>
          <h2 className={classes.title}>CARRER OBECTIVE</h2>
          <h5 className={classes.description}>
            Solution driven AWS Certified SysOps Administrator adept at contributing to highly collaborative 
            work environment with proven experience in Infrastructure (Cloud and On-prem), SaaS, Cyber Security, 
            and customer satisfaction with background on web application and database. Seeking for an 
            opportunity to leverage proven ability in DevOps domain.
            </h5>
        </GridItem>
        <GridItem xs={12} sm={12} md={8} justify="left">
          <h2 className={classes.title}>TECHNICAL SKILL</h2>
          <h5 className={classes.description}>
                <h4>Experience in CI/CD (Continuous Integration/ Continuous Deployment) process, Strong background in Windows Server Administration & Management and Cloud Implementation all within that suite the needs of an environment under DevOps Culture</h4>
                <h4>Understanding of SDLC, Traditional, Agile, and other methodologies</h4>
                <h4>Knowledge of version control system like Git and used source code management tools GitHub and Bitbucket including command line application</h4>
                <h4>Managed Amazon Web Services like Virtual Private cloud (VPC) EC2, S3 bucket, RDS, ELB, ASG, AMI, IAM and others</h4>
                <h4>Good Working experience with bash script</h4>
                <h4>Hand-on experience with database (MySQL, SQLite)</h4>
                <h4>Good knowledge of HTML5, CSS3, C and PHP</h4>
                <h4>Excellent understanding of Active Directory, DHCP, DNS, Remote Desktop, Exchange server and File Server</h4>
                <h4>Experience in administering SaaS, such as Microsoft Azure, Dropbox and Zoom</h4>
                <h4>Worked with FortiGate Firewall to enhance organizational security</h4>
                <h4>Experience in issue tracking tool like Jira</h4>
            </h5>
        </GridItem>
      </GridContainer>
      </div>
    );
}
